import React,{useState} from 'react'
import axios from "axios"

const CreateUser = () => {
    const [Inputs,setInputs] = useState({})

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

// Requiring module
const bcrypt = require('bcryptjs');

const password = 'pass123';
var hashedPassword;

// Encryption of the string password
bcrypt.genSalt(10, function (err, Salt) {

	// The bcrypt is used for encrypting password.
	bcrypt.hash(password, Salt, function (err, hash) {

		if (err) {
			return console.log('Cannot encrypt');
		}

		hashedPassword = hash;
		console.log(hash);

		bcrypt.compare(password, hashedPassword,
			async function (err, isMatch) {

			// Comparing the original password to
			// encrypted password
			if (isMatch) {
				console.log('Encrypted password is: ', password);
				console.log('Decrypted password is: ', hashedPassword);
			}

			if (!isMatch) {
				console.log(hashedPassword + ' is not encryption of '
				+ password);
			}
		})
	})
})


    const handleSubmit = (e) => {
        // console.log(Inputs);
        axios.post("http://localhost:3007/create-user",Inputs)
        .then((res) => {
            console.log(res.data)
        }).catch((error) => {
            console.log(error)
        });
    }

    return (
        <div style={{textAlign: 'center'}}>
        <h1>Registration form</h1>
        <form onSubmit={handleSubmit} >
            <div style={{marginTop: '40px'}}>
            <label>Enter name:</label>
                <input 
                type="text"
                name="name"
                value={Inputs.name} 
                onChange={handleChange}
                style={{marginLeft: '20px'}}
                required/>
            </div>
            <div style={{marginTop:'20px'}}>
            <label>Enter email:</label>
                <input 
                type="email"
                name="email"
                value={Inputs.email} 
                onChange={handleChange}
                style={{marginLeft: '20px'}}
                required/>
            </div>
            <div style={{marginTop:'20px'}}>
            <label>Enter Password:</label>
                <input 
                type="password"
                name="password"
                value={Inputs.password} 
                onChange={handleChange}
                style={{marginLeft: '4px'}}
                required/>
            </div>
            <div style={{marginTop:'20px'}}>
                <label style={{marginTop:'20px',marginLeft: '100px'}}>Upload Profile Pic</label>
                <input type='file' style={{marginLeft:'20px'}}/>
            </div>
                <button style={{marginTop:'20px'}}type="submit"> Submit </button>
        </form>
        </div>
    )
}

export default CreateUser
